  (() => {
    let alcoholType = appsmith.store.alcoholType;
    let price = JSON.parse(appsmith.store.Price);

    if (["Wine"].includes(alcoholType)) {
      return {
        "wineType": alcoholType,
        "flavorProfile": appsmith.store.tasteProfile,
        "priceRange": price
      };
    } else if (["Beer"].includes(alcoholType)) {
      return {
        "beerStyle": alcoholType,
        "tasteProfile": appsmith.store.tasteProfile,
        "cost": { "low": price.min, "high": price.max }
      };
    } else if (["Whiskey", "Vodka", "Rum", "Gin", "Tequila", "Brandy"].includes(alcoholType)) {
      return {
        "spiritCategory": alcoholType,
        "tasteNotes": appsmith.store.tasteProfile,
        "cost": { "low": price.min, "high": price.max }
      };
    } else {
      return {
        "cocktailName": alcoholType,
        "flavorProfile": appsmith.store.tasteProfile,
        "price": price.min
      };
    }
  })()
}}
}

